const SequelizeErrorHandlers = require('./SequelizeErrorHandlers');
const ERROR_MAP = require('./error_map');
const formatStackTrace = require('./formatStackTrace');
/**
 * SequelizeError represents a custom error
 * class to handle sequelize's exception in
 * a custom way it will parse the error into
 * a known format and extract the stack trace
 * into an pretty format.
 */
class SequelizeError extends Error {
  constructor(originalError) {
    super(originalError.message);
    this.errorHandlers = SequelizeErrorHandlers;
    const { status, code, message, details } = this.handleError(originalError);
    this.status = status;
    this.code = code;
    this.message = message;
    this.details = details;

    Error.captureStackTrace(this, this.constructor);
  }

  /**
   * Overrides the default toJSON behavior
   * to create a pretty printable json Object
   * @return {Object}
   */
  toJSON() {
    const { status, code, message, details } = this;
    return { status, code, message, details };
  }

  handleError(originalError) {
    let resultingError = {};
    const { name } = originalError;
    if (this.errorHandlers && this.errorHandlers[name]) {
      const errorHandler = this.errorHandlers[name];
      if (typeof errorHandler === 'function') {
        resultingError = errorHandler.call(this, originalError);
      } else if (typeof errorHandler === 'object') {
        const { message, stack } = originalError;
        const { status, code } = errorHandler;
        const details = formatStackTrace(stack);
        resultingError = { status, code, message, details };
      }
    } else if (name === 'ResourceError') {
      resultingError = originalError;
    } else {
      const { message, stack } = originalError;
      const { status, code } = ERROR_MAP.UNKNOWN;
      const details = formatStackTrace(stack);
      resultingError = { status, code, message, details };
    }
    return resultingError;
  }
}

module.exports = SequelizeError;

const ERROR_MAP = require('./error_map');
/**
 * Defines Sequelize error handling map,
 * on every sequelize's error it will map
 * the corresponding exception to a status code
 * present on error_map.js, except for
 * SequelizeValidationError, on this particular
 * case whe will not use formatStackTrace
 * to extract the detail field, it will
 * extract the errors from the original
 * exception and map them into a new detail
 *
 */
module.exports = {
  SequelizeValidationError: errorObj => {
    const { status, code } = ERROR_MAP.FAILED_PRECONDITION;
    const details = errorObj.errors.map(err => {
      const { type, message } = err;
      return { type, message };
    });
    return { status, code, message: 'Validation Failed', details };
  },
  SequelizeDatabaseError: ERROR_MAP.INTERNAL,
  SequelizeTimeoutError: ERROR_MAP.UNAVAILABLE,
  SequelizeForeignKeyConstraintError: ERROR_MAP.FAILED_PRECONDITION,
  SequelizeUniqueConstraintError: ERROR_MAP.ALREADY_EXISTS,
  SequelizeConnectionError: ERROR_MAP.UNAVAILABLE,
  SequelizeConnectionRefusedError: ERROR_MAP.UNAVAILABLE,
  SequelizeAccessDeniedError: ERROR_MAP.PERMISSION_DENIED,
  SequelizeHostNotFoundError: ERROR_MAP.INTERNAL,
  SequelizeHostNotReachableError: ERROR_MAP.INTERNAL,
  SequelizeInvalidConnectionError: ERROR_MAP.INTERNAL,
  SequelizeConnectionTimedOutError: ERROR_MAP.INTERNAL,
  SequelizeInstanceError: ERROR_MAP.INTERNAL,
  SequelizeEmptyResultError: ERROR_MAP.INTERNAL,
  SequelizeQueryError: ERROR_MAP.INTERNAL
};

const parseStackTrace = errorLine => {
  const resultingObj = {};
  const traceFields = {
    blamingFile: /\(\S+\)/,
    blamingLines: /:\d+:\d+/,
    blamingSource: /at \S+/
  };
  Object.keys(traceFields).forEach(traceField => {
    const traceRegexp = traceFields[traceField];
    const matchResult = errorLine.match(traceRegexp);
    if (matchResult && matchResult.length > 0) {
      const [firstMatch] = matchResult;
      resultingObj[traceField] = firstMatch;
    }
  });
  return resultingObj;
};

const transformStackTrace = errorLine => {
  let line = null;
  let column = null;
  let { blamingFile, blamingLines, blamingSource } = errorLine;
  if (blamingFile != null && typeof blamingFile === 'string') {
    blamingFile = blamingFile
      .replace('(', '')
      .replace(')', '')
      .replace(blamingLines, '');
  }
  if (blamingSource != null && typeof blamingFile === 'string') {
    blamingSource = blamingSource.replace('at', '').trim();
  }
  if (blamingLines != null) {
    blamingLines = blamingLines.split(':');
    [, line, column] = blamingLines;
  }
  return { blamingFile, line, column, blamingSource };
};

